import React, { Component } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import DateTimeRangePicker from "@wojtekmaj/react-datetimerange-picker";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { faSave } from "@fortawesome/free-solid-svg-icons";

class DisionDialoge extends Component {
  deleteEvent = e => {
    e.preventDefault();
    this.props.deleteEvent(e);
  };

  render() {
    return (
      <div class="container">
        <div class="row">
          <div class="col-sm">
            <h3>{this.props.title}</h3>
          </div>
        </div>
        <div className="row">
        <div className="col-sm">
        <form>
        <input type="text" placeholder="Title des Termins" className="text-input"/>
        <DateTimeRangePicker
            onChange={this.onChange}
            value={this.props.date}
            disableClock={true}
            className="calendar-self"
          />
        </form>
        </div>
        </div>
        <div className="row">
          <div className="col-sm">
            <button
              type="button"
              class="btn btn-danger btn-outline-primary mt-4"
              onClick={this.deleteEvent}
            >
              <FontAwesomeIcon icon={faTrash} className="icon" />
              &nbsp;&nbsp; Termin LÃ¶schen
            </button>
            <button type="button" class="btn btn-outline-primary mt-4">
              <FontAwesomeIcon icon={faSave} className="icon" />
              &nbsp;&nbsp;Speichern
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default DisionDialoge;
